import Board from "./Board"
import React from "react";

class Game extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          history: [{
            squares: Array(0).fill(null),
          }],
          stepNumber: 0,
          xIsNext: true,
          grid_num : 0,
          allow: false,
        };
        this.handleInput = this.handleInput.bind(this);
        this.handleButton = this.handleButton.bind(this);
    }

    calculateWinner(squares) {

      //vertical
      for (let i = 0; i < this.state.grid_num; i++) {


      }

      //horizontal
      for (let i = 0; i < this.state.grid_num; i++) {


      }

      //diagonal upper left - lower right
      for (let i = 0; i < this.state.grid_num; i++) {


      }

      //diagonal lower left - upper right
       for (let i = 0; i < this.state.grid_num; i++) {


      }
      const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6],
      ];
      for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i];
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
          return squares[a];
        }
      }
      return null;
    }

    handleInput(e){
        this.setState({...this.state,
          // history: [{
          //   squares: Array(e.target.value).fill(null)
          // }],
          grid_num: e.target.value,
          allow: false
        })

    }

    handleButton(){
        this.setState({...this.state,
          allow: true
        })
    }

    handleClick(i) {
        const history = this.state.history.slice(0, this.state.stepNumber + 1);
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        if (this.calculateWinner(squares) || squares[i]) {
          return;
        }
        squares[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
          history: history.concat([{
            squares: squares,
          }]),
          stepNumber: history.length,
          xIsNext: !this.state.xIsNext,
        });
    }

    jumpTo(step) {
      this.setState({
        stepNumber: step,
        xIsNext: (step % 2) === 0,
      });
    }

    render() {
        const history = this.state.history;
        const current = history[this.state.stepNumber];
        const winner = this.calculateWinner(current.squares);

        const moves = history.map((step, move) => {
          const desc = move ?
            'Go to move #' + move :
            'Go to game start';
          return (
            <li>
              <button onClick={() => this.jumpTo(move)}>{desc}</button>
            </li>
          );
        });

        let status;
        if (winner) {
        status = 'Winner: ' + winner;
        } else {
        status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
        }

        return (
            <div className="game">
              <div className="input">
                <input
                  type="number"
                  name="inputField"
                  onChange={this.handleInput}
                  // ref={node => (this.inputNode = node)}

                />
                <button onClick={this.handleButton} className="btn btn-success">
                  Create
                </button>
              </div>
              <div className="game-board">
                <Board
                  squares={current.squares}
                  grid_num={this.state.grid_num}
                  allow={this.state.allow}
                  onClick={(i) => this.handleClick(i)}
                />
              </div>
              <div className="game-info">
                <div>{status}</div>
                <ol>{moves}</ol>
              </div>
            </div>
        );
    }
  }

export default Game;
